changeTopN(e, n, url) {
    var param_topN = null;
    var param_from = null;
    var param_to = null;
    var param_agg = null;
    switch(url){
        case "mockup_3_1":
            this.state.m_3_1.topN = n;
            param_topN = n;
            param_agg = this.state.m_3_1.agg;
            param_from = this.state.m_3_1.from;
            param_to = this.state.m_3_1.to;
            break;
    }
    this.setState({state: this.state});
    if(param_from != "select" && param_to != "select"){
        fetch(`http://127.0.0.1:5000/${url}`, {
            method: 'POST',
            body: JSON.stringify({
                topN: param_topN,
                from: param_from,
                agg: param_agg,
                to: param_to
              // Add parameters here
            }),
            headers: {
              'Content-type': 'application/json; charset=UTF-8',
            },
          })
             .then((response) => response.json())
             .then((data) => {
                console.log(data);
                switch(url){
                    case "mockup_3_1":
                      var temp = this.state.m_3_1;
                      temp.data = data.data;
                      this.setState({m_3_1:temp});
                      break;
                  }
                // Handle data
             })
             .catch((err) => {
                console.log(err.message);
             });
    }
  }

