/*!

=========================================================
* Argon Design System React - v1.1.2
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-design-system-react
* Copyright 2023 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-design-system-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import { Link } from 'react-router-dom';
// nodejs library that concatenates classes

// reactstrap components
import {
  Button,
  Card,
  CardBody,
  Container,
  Row,
  Col,
} from "reactstrap";

// core components
import Navbar from "../components/NavBar.js";
import Footer from "../components/Footer.js";


class Landing extends React.Component {
  state = {};
  componentDidMount() {
    document.documentElement.scrollTop = 0;
    document.scrollingElement.scrollTop = 0;
    this.refs.main.scrollTop = 0;
  }
  render() {
    return (
      <>
        <Navbar />
  <main ref="main">
    <section className="section section-lg pt-lg-0 mt--200">
      <Container>
        <Row className="justify-content-center">
          <Col lg="12">
            <Row className="row-grid">
              {/* Card Sections */}
              <Col lg="4">
                <Card className="shadow border-0">
                  <CardBody className="py-5">
                    <img
                      alt="Global Temperatures"
                      className="img-fluid"
                      src={require("../assets/img/bear.jpg")}
                    />
                    <h6 className="text-primary text-uppercase">
                      Global Temperatures
                    </h6>
                    <p className="description mt-3">
                      Global Heat on the Move: How Temperature Swings in Different Countries over years?
                    </p>
                    <Link to="/GlobalTemp">
                      <Button className="mt-4" color="primary">
                        Learn more
                      </Button>
                    </Link>
                  </CardBody>
                </Card>
              </Col>
              {/* More Cards... */}
              {/* Similar structure for other cards (CO2 Emissions, Energy Usage, etc.) */}
            </Row>
          </Col>
        </Row>
      </Container>
    </section>

    <div className="position-relative">
      {/* shape Hero */}
      <section className="section section-lg section-shaped pb-250">
        <div className="shape shape-style-1 shape-default">
          {/* Shapes */}
        </div>
        <Container className="py-lg-md d-flex">
          <div className="col px-0">
            <Row>
              <Col lg="6">
                <h1 className="display-3 text-black">
                  Global Warming - Deep Dive{" "}
                </h1>
                <p className="lead text-black">
                  A deep dive into the causes and consequences of Global Warming spanning Economic factors, internet consumption, global temperatures and more ...
                </p>
              </Col>
            </Row>
          </div>
        </Container>
      </section>
    </div>
  </main>
  <Footer />
</>

    );
  }
}

export default Landing;
